// gets the last two digits of a number or string-number
const getLastTwoDigits = (string: string) => String

// removes the last two digits of a number or string-number
const removeLastTwoDigits = (string: string) => String

// adds thousand separators (, or .) on numbers or string-numbers
const addThousandsSeparator = (value: string | number, useComma?: boolean) =>
  String

// parses string or numbers to accounting format string-numbers
export const toAccountingFormat = (
  value: string | number,
  options?: { useComma?: boolean }
) => String
